import Test from "./test";
import React from 'react';

const {useState} = React;
function App() {

  const [names,setNames] = useState([
    {id:1,name:"안녕"},
    {id:2,name:"안녕2"}




]);

     const [inputText,setInputText] = useState("");
     const [nextId,setNextId] = useState(4); // 4가머지

     const onChangeHandler = e => setInputText(e.target.value);
     const onClickHandler = ()=>{
      const changeNames = names.concat({ // 햇갈림
        id:nextId,
        name:inputText
      });


      setNames(changeNames);
      setNextId(nextId+1);
      setInputText("");
   }

   const onRemove = (id) => {
    const changeNames = names.filter(name => name.id !== id); // 트루일때만 반환
    setNames(changeNames);
}



  const nameList = names.map(name =>{
  return (<li key={name.id} type ="checkbox"> {name.name}<button onClick ={()=>onRemove(name.id)}>삭제</button></li>) //이구분이해가 좀 안됨
});


  return (
   <>

   <h1>Todolist</h1>
   <Test/>
   <button>다크모드</button>
   <ul>
       {nameList}
   </ul>
   <input type="text" value={inputText} onChange={onChangeHandler}/>
                    <button onClick={onClickHandler}>추가</button>
   </>
  );
}

export default App;
