function App() {

  const [names,setNames] = useState([
    {id:1,name:"안녕"},
    {id:2,name:"안녕2"}




]);

     const [inputText,setInputText] = useState("");
     const [nextId,setNextId] = useState(3); // 4가머지
     const [color,setColor] = useState("");
     const [color1,setColor1] = useState("");
     const [backgroundColor,SetBackgound] = useState("");
     const [underLine,SetLine] = useState("");

     const style1 = {
      color : color,
      backgroundColor : backgroundColor 
     }

     const [Check,SetCheck] =useState(0);

     const onChangeHandler = e => setInputText(e.target.value);
     const onClickHandler = ()=>{
      const changeNames = names.concat({ // 햇갈림
        id:nextId,
        name:inputText,

      });


      setNames(changeNames);
      setNextId(nextId+1);
      setInputText("");
   }

   const onRemove = (id) => {
    const changeNames = names.filter(name => name.id !== id); // 트루일때만 반환
    setNames(changeNames);
}
   const onChangeColor = (e) =>{
    setColor1(e.target.value);
   }
   const onClickColor = ()=>
   {

      setColor(color1);
   }
   const onCheck = (e)=>
   {
     SetCheck(e.target.value);
   }

   const checkHandler= () => {
    //SetLine(text-decoration-color : overline "red")

   }
   const Background = () =>{
    SetBackgound("black");
    setColor("white");
    style1.backgroundColor = backgroundColor;
    style1.color = color;
   }
  const nameList = names.map(name =>{
  return (<><input type="checkBox" onChange={()=>checkHandler()}/><li key={name.id}> {name.name} <button onClick ={()=>onRemove(name.id)}>삭제</button></li></>) //이구분이해가 좀 안됨
});


  return (
   <div style = {{backgroundColor}} >

   <h1 style={{color}}>Todolist</h1>
   <Test/>
   <button onClick ={Background}>다크모드</button>
   <ul  style={{color}}>
       {nameList}
   </ul>
   <input type="text" value={inputText} onChange={onChangeHandler}/>
                    <button onClick={onClickHandler}>추가</button>
    <input type="text" value={color1} onChange={onChangeColor}/> <button onClick={onClickColor} >색변경</button>
   </div>
  );
}

export default App;
