import { useState } from "react";



const Notices = ({}) =>
{

    return (<>
        <li>
            <h1>{current.title}</h1>
                {current.detail}
        </li>
        <button onClick={()=>ondelete(current.title)}>삭제</button>
        </>)
}




export const NoticeBoard = () => {

    const [user,setUser] = useState({title:'',detail:''});
    const [user1,setUser1] = useState([]);


    const onChangeHandler = e =>
        {
            setUser({
                ...user,                          // 유저 전체를 가져와서 참조하고 e.target.name이 변하는곳을 밸류로 채워줌
                [e.target.name] : e.target.value 
            })
        }
    const onClicker =()=>
    {
        

        setUser1((prevUser1) => [...prevUser1, user]);
  
    }
    const ondelete = (id) =>{
        setUser1(user1.filter((us)=>us.title !== id));
    }

    return(
        <>
            <h1>게시판</h1>
             
            <input type="text" name="title" value={user.title}  placeholder="제목" onChange={onChangeHandler}/>
            <br/>
            <br/>
            <input type="text" name="detail"value={user.detail} placeholder="내용" onChange={onChangeHandler}/>
            <br/>
            <button onClick={onClicker}>게시</button>
            <ul>
                {user1.map((current)=>{
                        return (<>
                        <li>
                            <h1>{current.title}</h1>
                                {current.detail}
                        </li>
                        <button onClick={()=>ondelete(current.title)}>삭제</button>
                        </>
                    )})}
            </ul>
               
        </>
    )
}
