import {useState} from "react";
import {useEffect} from "react";

const TestTimer = () =>{
    const[time,setTime] = useState(0);

    useEffect(()=>{
     
        const timer = setInterval(()=>{
            console.log(new Date().toLocaleTimeString());
        },(1000));

        return (()=>{
            clearInterval(timer); //끝나는시점에 한다?
            console.log("타이머 끝남");
        }
    )
    },[])


    return (
        <>
          <h1>{time}</h1>
        </>
    )
}


export const TestContainer = ()=>
{
    return(
    <TestTimer/>
    )
}
