import { useReducer } from "react";

const products = [
    { name: "아이폰 16", category: "전자제품" },
    { name: "맥북 프로", category: "전자제품" },
    { name: "에어팟", category: "전자제품" },
    { name: "삼성 갤럭시", category: "전자제품" },
    { name: "나이키 운동화", category: "의류" },
    { name: "아디다스 티셔츠", category: "의류" },
    { name: "믹서기", category: "가전제품" },
    { name: "전자레인지", category: "가전제품" },
  ];

  const reducer = (state,action) =>
  {
    switch(action.category){
        case "전자제품" :
         return {...state,[action.category] : action.name}
         case "의류" :
         return {...state,[action.category] : action.name}
         case "가전제품" : 
         return {...state,[action.category] : action.name} 
         default :
         return {...state,[action.category] : action.name}

    }
  }



  export const Product = ()=>{

    const [state,dispatch] = useReducer(reducer,products);






    return(
        <>
            <h1>상품카테고리쓰</h1>
            <button onClick={()=>dispatch({})}>모든 카테고리</button>
            <button onClick={()=>dispatch({category:"전자제품"})}>전자제품</button>
            <button onClick={()=>dispatch({category:"의류"})}>의류</button>
            <button onClick={()=>dispatch({category:"전자제품"})}>가전제품</button>
            <li>{state.map}</li>
        </>
    )
  }
