import { useState } from "react";



export const NoticeBoard = () => {

    const [user,setUser] = useState({title:'',password:''});
    const [user1,setUser1] = useState({title:'',password:''});


    const onChangeHandler = e =>
        {
            setUser({
                ...user,                          // 유저 전체를 가져와서 참조하고 e.target.name이 변하는곳을 밸류로 채워줌
                [e.target.name] : e.target.value 
            })
        }
    const onClicker =()=>
    {
        setUser1(user);
    }

    return(
        <>
            <h1>게시판</h1>
             
            <input type="text" name="username" value={user.title}  placeholder="제목" onChange={onChangeHandler}/>
            <br/>
            <br/>
            <input type="text" name="password"value={user.password} placeholder="내용" onChange={onChangeHandler}/>
            <br/>
            <button onClick={onClicker}>게시</button>
            <h3> : {user1.username}</h3>
            <h3> : {user1.password}</h3>
        </>
    )
}
